script:
frappe.ui.form.on('Attendance', {
	refresh(frm) {
		frm.add_custom_button(
            __('Import From Lark'),function(){

                frappe.call({method:'xsi_lark_poc.xsi_lark_poc.util.lark_attendance.fetch_entries', args:{
                    employee:frm.doc.employee,
                    date:frm.doc.attendance_date
                },
                callback:function(r){
                    console.log(r.message);
                    frm.doc.status=r.message
                    frm.refresh_field('status');
                }
                });

        }
	)}
});
